import React from 'react';
import './home.css'
import { FaGraduationCap, FaUserGraduate } from 'react-icons/fa';
import { GrUnorderedList } from 'react-icons/gr'

import Sidebar from '../../Components/Sidebar/sidebar'
import Card from '../../Components/Card/card'
import Rectangle from '../../Components/Rectangle/rectangle'
import Cardlist from '../../Components/CardList/cardlist'

const recs = [
    { quantity: "02", status: "Cursos matriculados", icon: <FaGraduationCap/> },
    { quantity: "02", status: "Atividades próximas", icon: <GrUnorderedList/> },
    { quantity: "785", status: "Alunos online", icon: <FaUserGraduate/>  },
]

const reclist = recs.map((rec, index) => (
    <Rectangle
        quantity={rec.quantity}
        status={rec.status}
        icon={rec.icon}
        key={index}
    />
))

const cards = [
    { image: "https://i.imgur.com/aadwyfC.jpg", course: "Algoritmos e Programação", tittle: "Enviar arquivo Peter Smokes", deadline: "10/10/21"},
    { image: "https://i.imgur.com/RgQrlAS.jpg", course: "Projeto de Banco de Dados", tittle: "Enviar o esquema MySQL", deadline: "10/10/21"},
]

const cardsList = cardsmap((card, index) => (
    <List
        card={card}
        key={index}
    />
))

const sidebar = {
    "dashboard": "Dashboard",
    "myarea": "Meus cursos",
};

const status = {
    "status": "Cursos Matriculados",
}

const nextActivities = [
    {
        "course": "Algoritmos e Programação",
        "title": "Enviar arquivo Peter Smokes",
        "image": "https://i.imgur.com/aadwyfC.jpg",
        "deadline": "10/10/21"
    },
    {
        "course": "Projeto de Banco de Dados",
        "title": "Enviar o esquema MySQL",
        "image": "https://i.imgur.com/RgQrlAS.jpg",
        "deadline": "10/10/21"
    },
];



const Home = ({ user }) => (

    <div className="home-conteiner">

        {/* <div className="home-dashboard">
          <Sidebar user={user} sidebar={sidebar} />
        </div>
        */}
        <div className="home-middle">
            <h1 className="home-resume">Resumo</h1>
            <div className="cards-list">{reclist}</div>
            <h1 className="home-next">Próximas atividades</h1>
            <div>
                <Card user={user} nextActivities={nextActivities} />
            </div>
        </div>

    </div>
)

export default Home;
