import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './fetchcourse.css'

import { FaGraduationCap, FaUserGraduate } from 'react-icons/fa';
import { GrUnorderedList } from 'react-icons/gr'

import Card from '../../Components/Card/card'
import Rectangle from '../../Components/Rectangle/rectangle'

const Course = ({ courses }) => {
    const [data, setData] = useState([]);
    const { id } = useParams();
    useEffect(() => {
        fetchCourse();
    }, []);
    const fetchCourse = () => {
        axios
            .get(
                `https://processo-seletivo-hut8.herokuapp.com/courses/${id}`
            )
            .then((res) => {
                setData(res.data);
                //console.log(res.data);
            })
            .catch((err) => console.log(err));
    };

    const recs = [
        { quantity: courses?.overview?.nextActivities, status: "Atividades próximas", icon: <GrUnorderedList/> },
        { quantity: courses?.overview?.credits, status: "Créditos", icon: <FaGraduationCap/> },
        { quantity: courses?.overview?.enrolledStudents, status: "Alunos matrículados", icon: <FaUserGraduate/>  },
    ]

    const reclist = recs.map((rec, index) => (
        <Rectangle
            quantity={rec.quantity}
            status={rec.status}
            icon={rec.icon}
            key={index}
        />
    ))

    return (

        <div key={data.id} className="fetch-container">
            {console.log(reclist)}
            <div className="fetch-usingData">
                <div className="fetch-usingData_resume">
                    <h1>{data?.course}</h1>
                    <p>{data?.class}</p>
                </div>
                    <div className="fetch-usingData_rectangle">{reclist}</div>
                </div>
                <div className></div>
        </div>

    );    
};

export default Course;
