import React, { useState, useEffect } from 'react';
import './card.css';
import { NavLink } from 'react-router-dom';
import axios from 'axios';

function Card(props) {

    const [cards, setCards] = useState([]);
    useEffect(() => {
        fetchCards();
    }, []);
    const fetchCards = () => {
        axios
            .get('https://processo-seletivo-hut8.herokuapp.com/courses')
            .then((res) => {
                console.log(res);
                setCards(res.data);
            })
            .catch((err) => {
                console.log(err);
            });
    };

    return (
        <div className="card">
            {cards.map((property) => (
                <NavLink exact to={`/courses/${property.id}`} className="courses-course_button" activeClassName="courses-course_button_active" >
                    <img src={props.image} alt="Imagem Ilustrativa" className="card-image" />
                    <div className="card-underRectangle">
                            <div className="card-type">{props.type}</div>
                            <p className="card-subject">{props.course}</p>
                            <div className="card-class">{props.class}</div>
                            <h1 className="card-task">{props.title}</h1>
                            <p className="card-deadline">{props.deadline}</p>
                    </div>
                    </NavLink>
                ))}
            </div>
    )
};

export default Card;
